<template>
  <div class="login-bg">
    <a-card title="Zookeeper Admin" size="300" class="login-card">      
      <a-form
        :model="formState"
        name="normal_login"
        class="login-form"
        autocomplete="off"
        @finish="onFinish"
      >
      <a-form-item
        name="username"
        :rules="[{ required: true, message: 'Please input your username!' }]"
      >
          <a-input v-model:value="formState.username">
            <template #prefix>
              <UserOutlined class="site-form-item-icon" />
            </template>
          </a-input>
        </a-form-item>

        <a-form-item
          name="password"
          :rules="[{ required: true, message: 'Please input your password!' }]"
        >
          <a-input-password v-model:value="formState.password">
            <template #prefix>
              <LockOutlined class="site-form-item-icon" />
            </template>
          </a-input-password>
        </a-form-item>
        <a-form-item>
          <a-button type="primary" html-type="submit" :loading="formState.inLogin" class="login-form-button">
            Log in
          </a-button>
        </a-form-item>
      </a-form>
    </a-card>
  </div>
  
</template>
<script setup>
  import { reactive } from 'vue';
  import { UserOutlined, LockOutlined } from '@ant-design/icons-vue'
  import { login } from '@/request/api/user'
  import { setToken } from '@/utils'
  const formState = reactive({
    username: '',
    password: '',
    inLogin: false
  });
  const onFinish = async(values) => {
    try{
      formState.inLogin = true
      let res = await login(values);
      setToken(res.data.data.token)
      window.location.href = "/"
      return false
    } catch(err) {
      formState.inLogin = false
    }
  }
  
</script>

